mapper.select:
select

from public.仕入データ
where 仕入番号 = #{仕入番号,jdbcType=VARCHAR}

select

from public.仕入データ


        仕入番号 = #{purchaseNumber,jdbcType=VARCHAR}


        and 仕入日 = #{purchaseDate,jdbcType=TIMESTAMP}


        and 発注番号 = #{purchaseOrderNumber,jdbcType=VARCHAR}


        and 仕入先コード = #{supplierCode,jdbcType=VARCHAR}


        and 仕入先枝番 = #{supplierBranchNumber,jdbcType=INTEGER}


        and 仕入担当者コード = #{purchaseManagerCode,jdbcType=VARCHAR}


        and 部門コード = #{departmentCode,jdbcType=VARCHAR}
