mapper.select:
select

from public.発注データ
where 発注番号 = #{発注番号,jdbcType=VARCHAR}

select

from public.発注データ


        発注番号 = #{purchaseOrderNumber,jdbcType=VARCHAR}


        and 発注日 = #{purchaseOrderDate,jdbcType=TIMESTAMP}


        and 受注番号 = #{salesOrderNumber,jdbcType=VARCHAR}


        and 仕入先コード = #{supplierCode,jdbcType=VARCHAR}


        and 仕入先枝番 = #{supplierBranchNumber,jdbcType=INTEGER}


        and 発注担当者コード = #{purchaseManagerCode,jdbcType=VARCHAR}


        and 指定納期 = #{designatedDeliveryDate,jdbcType=TIMESTAMP}


        and 倉庫コード = #{warehouseCode,jdbcType=VARCHAR}


        and 備考 = #{remarks,jdbcType=VARCHAR}
