mapper.select:
select

from public.支払データ
where 支払番号 = #{支払番号,jdbcType=VARCHAR}

select

from public.支払データ


        支払番号 = #{paymentNumber,jdbcType=VARCHAR}


        and 支払日 = #{paymentDate,jdbcType=INTEGER}


        and 仕入先コード = #{supplierCode,jdbcType=VARCHAR}


        and 仕入先枝番 = #{supplierBranchNumber,jdbcType=INTEGER}


        and 部門コード = #{departmentCode,jdbcType=VARCHAR}


        and 支払方法区分 = #{paymentMethodType,jdbcType=INTEGER}


        and 支払完了フラグ = #{paymentCompletedFlag,jdbcType=INTEGER}
